using System;using System.Collections.Generic;namespace ECM.Core{    public static class Data    {        static Dictionary<string, Account> m_Accounts = new Dictionary<string, Account>();        static Dictionary<long, Character> m_Characters = new Dictionary<long, Character>();        public static  Dictionary<string, Account> Accounts        {            get { return m_Accounts; }        }        public static Dictionary<long, Character> Characters        {            get { return m_Characters; }        }        public static void UpdateOnHeartbeat()        {            foreach(Account account in m_Accounts.Values)            {                account.UpdateOnHeartbeat();            }            foreach(Character character in m_Characters.Values)            {                character.UpdateOnHeartbeat();            }        }    }}